template.javascript:
function ListNode(val, next = null) {
  this.val = val;
  this.next = next;
}

function listToLinkedlist(arr) {
  let dummy = new ListNode(0);
  let curr = dummy;
  for (const v of arr) {
    curr.next = new ListNode(v);
    curr = curr.next;
  }
  return dummy.next;
}

function linkedlistToList(head) {
  const res = [];
  while (head) {
    res.push(head.val);
    head = head.next;
  }
  return res;
}

